mapper.update:
UPDATE courses c
SET c.select_people = c.select_people + 1
WHERE c.cno = #{cno};

UPDATE courses c
SET c.select_people = c.select_people - 1
WHERE c.cno = #{cno};

UPDATE course_selection sc
SET grade = #{grade}
WHERE sc.sno = #{sno}
    AND sc.cno = #{cno}

mapper.delete:
DELETE FROM course_selection sc
WHERE sc.cno = #{cno}
AND sc.sno = #{sno}

mapper.select:
SELECT c.cno, c.cname, c.credit, l.dname, t.tname, c.time, c.startWeek, c.endWeek, c.dayOfWeek, c.total_people - c.select_people as remainPeople, IF(cs.sno IS NOT NULL, '已选择', '未选择') AS flag FROM courses c LEFT JOIN course_selection cs ON c.cno = cs.cno AND cs.sno = #{sno} -- 替换为具体sno LEFT JOIN course_locations cl ON c.cno = cl.cno LEFT JOIN locations l ON cl.dno = l.dno LEFT JOIN teaching tch ON c.cno = tch.cno LEFT JOIN teachers t ON tch.tno = t.tno WHERE c.type = '选修' -- 筛选选修课
AND c.dayOfWeek = #{dayOfWeek}
AND c.time = #{time}
ORDER BY c.cno;

SELECT count(sno)
FROM course_selection sc
         INNER JOIN courses c ON sc.cno = c.cno
WHERE sc.sno = #{sno}
  AND c.type = '选修';
